import React, { useState, useEffect } from "react";
import Search from "./components/search";
import Select from "./components/select";
import Table from "./components/table";

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch('/api')
      .then(res => res.json())
      .then(res => setData(res.table))
  }, []);

  const sortData = (field) => {
    field == 'default' ? setData(data) : 
    console.log(field)
    const copiedData = data.concat()
    const sortedData = copiedData.sort(
      (a, b) => {return (field != 'decrease' || field != 'default' ? a[field] > b[field] : a['id'] < b['id']) ? 1 : -1}
    )
    setData(sortedData)
  }

  !data ? console.log("Loading...") : data.forEach(el => { console.log(el); })

  return (
    <div className="container">
      <div>
        <Search />
        <Select choice = {'field'} sortData={sortData}/>
        <Select sortData={sortData}/>
      </div>
      <Table data={data} />
    </div>
  );
}

export default App;
