import React, { useState, useEffect } from "react";
import Search from "./components/search";
import Select from "./components/select";
import Table from "./components/table";

function App() {
  const [data, setData] = useState([])
  const [increase, setIncrease] = useState(true)

  useEffect(() => {
    fetch('/api')
      .then(res => res.json())
      .then(res => setData(res.table))
  }, []);

  const sortData = (field) => {
    if (field == 'decrease') { setIncrease(false) }
    if (field == 'increase') { setIncrease(true) }
    const copiedData = data.concat()
    const sortedData = copiedData.sort(
      (a, b) => {return (increase ? a[field] > b[field] : a[field] < b[field]) ? 1 : -1}
    )
    setData(sortedData)
  }

  const filteredData = (query) => {
    const copiedData = data.concat()
    copiedData.filter(post => {
      if (query === '') {
      return post
      } else if (post.toLowerCase().includes(query.toLowerCase())) {
        return post
      }
      else {
        copiedData.pop(post)
      }
  })
  }

  !data ? console.log("Loading...") : data.forEach(el => { console.log(el); })

  return (
    <div className="container">
      <div>
        <Search filteredData={filteredData}/>
        <Select choice = {'field'} sortData={sortData}/>
        <Select sortData={sortData}/>
      </div>
      <Table data={data} />
    </div>
  );
}

export default App;
